mapper.select:
SELECT *
FROM asset
WHERE

    user_id = #{userId}


    home_serial_number = #{homeSerialNumber}


    AND name LIKE CONCAT('%',#{searchValue},'%')


    AND asset_type_id in

        #{assetType}



    AND purchase_date = #{searchValue}


    AND purchase_price = #{searchValue}


    AND current_value = #{searchValue}


    AND notes LIKE CONCAT('%',#{searchValue},'%')

SELECT *
FROM asset
WHERE
asset_type_id in (1,2,3,4,5) AND

    user_id = #{userId}


    home_serial_number = #{homeSerialNumber}

SELECT *
FROM asset
WHERE
asset_type_id in (6,7,8,9,10) AND

    user_id = #{userId}


    home_serial_number = #{homeSerialNumber}

SELECT asset_serial_number
FROM asset
WHERE

    user_id = #{userId}


    home_serial_number = #{homeSerialNumber}

AND asset_serial_number = #{assetSerialNumber}

mapper.update:
UPDATE asset
SET home_serial_number = #{homeSerialNumber}
WHERE user_id = #{requestId}
