mapper.select:
SELECT *
FROM finance
where user_id = #{userId}

    AND name LIKE CONCAT('%',#{searchValue},'%')


    AND asset_serial_number LIKE CONCAT('%',#{searchValue},'%')


    AND date = #{searchValue}


    AND amount = #{searchValue}


    AND notes LIKE CONCAT('%',#{searchValue},'%')


    AND type = 1


    AND type = 0
