mapper.update:
UPDATE home_request
SET status = 1
WHERE home_serial_number = #{homeSerialNumber}
AND join_id = #{joinId}

UPDATE home_request
SET status = 2
WHERE home_serial_number = #{homeSerialNumber}
AND application_id = #{joinId}

mapper.select:
SELECT *
FROM home_request
WHERE home_serial_number = #{homeSerialNumber}
AND application_id = #{userId}
